select * from (
  select total_active_per_quarter.quarter as quarter, total_active, (sum(num_registrations) over(order by coalesce(total_active_per_quarter.quarter, total_registrations_per_quarter.quarter))) - total_active as total_inactive
  from (
    select to_char(date_trunc('month', created_at), 'YY/"Q"Q') as quarter, count(*) as num_registrations
    from customers
    group by quarter order by quarter desc
  ) total_registrations_per_quarter
  full outer join (
    select to_char(date_trunc('month', created_at), 'YY/"Q"Q') as quarter, count(distinct customer_id) as total_active
    from jobs_result
    group by quarter order by quarter
  ) total_active_per_quarter
  ON total_registrations_per_quarter.quarter=total_active_per_quarter.quarter
) result
WHERE to_date(quarter, 'YY/"Q"Q') >= '${__from:date:YYYY-MM-DD HH:mm:ss}' AND to_date(quarter, 'YY/"Q"Q') <= '${__to:date:YYYY-MM-DD HH:mm:ss}';

